import { Client } from '@stomp/stompjs'
import SockJS from 'sockjs-client'

let stompClient = null
let isConnected = false

export function connect(userId, onMessageReceived) {
    const socket = new SockJS('http://192.168.1.104:8090/ws-chat')
    stompClient = new Client({
        webSocketFactory: () => socket,
        connectHeaders: { userId }, // ✅ 绑定 Principal
        debug: str => console.log(str)
    })

    stompClient.onConnect = () => {
        console.log('STOMP connected')
        isConnected = true

        // 群聊
        stompClient.subscribe('/topic/group', msg => onMessageReceived(JSON.parse(msg.body)))

        // 私聊
        stompClient.subscribe(`/user/${userId}/queue/private`, msg => onMessageReceived(JSON.parse(msg.body)))
    }

    stompClient.onStompError = frame => {
        console.error('STOMP broker error: ' + frame.headers['message'])
        console.error('Details: ' + frame.body)
    }

    stompClient.activate()
}

export function sendMessage(fromUserId, toUserId, content, tempId) {
    if (!stompClient || !isConnected) {
        console.error('STOMP not connected yet!')
        return
    }

    const payload = { fromUserId, toUserId, content, tempId } // ✅ 包含 tempId
    stompClient.publish({
        destination: '/app/chat.send',
        body: JSON.stringify(payload)
    })
}
