import axios from 'axios'
import { ElMessage } from 'element-plus'

// 创建 axios 实例
const egg = axios.create({
    baseURL: 'http://localhost:8090',
    timeout: 10000
})

// 请求拦截器：自动带 token
egg.interceptors.request.use(
    config => {
        const token = localStorage.getItem('token')
        if (token) {
            config.headers['Authorization'] = 'Bearer ' + token
        }
        return config
    },
    error => Promise.reject(error)
)

// 响应拦截器：自动解封装 Result + 全局错误弹窗
egg.interceptors.response.use(
    response => {
        const res = response.data
        if (res.success) {
            return res.data   // 成功直接返回 data
        } else {
            ElMessage.error(res.message || '请求失败') // ✅ 全局弹窗
            return Promise.reject(new Error(res.message || 'Error'))
        }
    },
    error => {
        ElMessage.error(error.message || '网络错误') // ✅ 网络错误也弹窗
        return Promise.reject(error)
    }
)

export default egg
