import { createApp } from 'vue'
import App from './App.vue'
import router from './router'

// 引入 Element Plus
import ElementPlus from 'element-plus'
import 'element-plus/dist/index.css'

// 创建 Vue 实例并挂载插件
const app = createApp(App)

app.use(router)
app.use(ElementPlus)

app.mount('#app')

// ------------------------
// 屏蔽 ResizeObserver loop 错误
// ------------------------

// 1. 包装 ResizeObserver.prototype.observe，捕获异常
const resizeObserverProto = ResizeObserver.prototype
const originalObserve = resizeObserverProto.observe
resizeObserverProto.observe = function (...args) {
    try {
        return originalObserve.apply(this, args)
    } catch (e) {
        if (e.message.includes('ResizeObserver loop limit exceeded')) {
            // 屏蔽该错误
            return
        }
        throw e
    }
}

// 2. 全局捕获 ResizeObserver loop 错误，不打印到控制台
window.addEventListener('error', (e) => {
    if (e.message.includes('ResizeObserver loop')) {
        e.stopImmediatePropagation()
        console.warn('ResizeObserver loop 被忽略')
    }
})
