import axios from 'axios';
export default {
  name: 'UserRegister',
  data() {
    return {
      form: {
        username: '',
        email: '',
        password: '',
        age: null,
        gender: null
      }
    };
  },
  methods: {
    submit() {
      axios.post('/auth/register', this.form).then(res => {
        if (res.data === '注册成功') {
          this.$message.success(res.data);
        } else {
          this.$message.error(res.data);
        }
      }).catch(err => {
        this.$message.error('注册请求失败');
        console.error(err);
      });
    },
    reset() {
      this.$refs.registerForm.resetFields();
    }
  }
};
